INSERT INTO `detail` VALUES ('3', '1', null, '4月1日，翟欣欣发来一段几只鸟飞过别墅的视频，苏享茂表示惊讶，感受到压力。他展示了股票账户和理财账户，借此表明自己也买得起别墅。翟欣欣对苏享茂透露一见钟情，表示愿意替他生孩子。', null, null, '1', '1', '2017-04-01 00:00:00', '1', '0', '0', '1', '2017-10-28 11:04:42', '2017-10-28 11:04:42');
